	function setExerciseTables($con)
	{
		$query = mysqli_query($con, "SELECT * FROM users WHERE username = '".$_SESSION['userLogged']."'");
		$row = mysqli_fetch_array($query);

		$_SESSION['tables'] = $row['exerciseTables'];
	}
